targets.expr:
sum by (instance)(
  gas_used{job="$job", instance=~"$instance(:\\d+)?$"}
)
/
on(instance)
  sum by (instance)(
    gas_limit{job="$job", instance=~"$instance(:\\d+)?$"} 
  )

scalar(
  max(
    ( max without(instance) (
        last_over_time(block_number{job="$job", instance=~"$instance(:\\d+)?$"}[$__rate_interval])
      )
      == bool
      min without(instance) (
        last_over_time(block_number{job="$job", instance=~"$instance(:\\d+)?$"}[$__rate_interval])
      )
  ))
)

sum by (instance)(
  gas_used{job="$job", instance=~"$instance(:\\d+)?$"}
)
/
on(instance) ( 
  sum by (instance)(
    gigagas{job="$job", instance=~"$instance(:\\d+)?$"} 
  ) * 1000000000
)

scalar(
  max(
    ( max without(instance) (
        last_over_time(block_number{job="$job", instance=~"$instance(:\\d+)?$"}[$__rate_interval])
      )
      == bool
      min without(instance) (
        last_over_time(block_number{job="$job", instance=~"$instance(:\\d+)?$"}[$__rate_interval])
      )
  ))
)

max without(instance) (
sum by (instance)(
  gas_used{job="$job", instance=~"$instance(:\\d+)?$"}
)
/
on(instance)
  sum by (instance)(
    gas_limit{job="$job", instance=~"$instance(:\\d+)?$"} 
  )
)

(
  # Calculates the Execute Block first and removes zeroes generated by idelta to smooth the panel
  sum by (instance, function_name) (
    clamp_min(
      idelta(function_duration_seconds_sum{job="$job", instance=~"$instance(:\\d+)?$", namespace!~"rpc|engine", function_name="Execute Block"}[$__rate_interval]),
      0
    )
  )
) > 0
or
(
# Calculates the rest of the instruments, except Execute Block, which was calculated before
  sum by (instance, function_name) (
    clamp_min(
      idelta(function_duration_seconds_sum{job="$job", instance=~"$instance(:\\d+)?$", namespace!~"rpc|engine", function_name!~"Execute Block|Block execution"}[$__rate_interval]),
      0
    )
  )
)
or
label_replace(
  sum by (instance) (
    clamp_min(
      idelta(function_duration_seconds_sum{job="$job", instance=~"$instance(:\\d+)?$", namespace!~"rpc|engine", function_name="Block execution"}[$__rate_interval]),
      0
    )
  )
  - sum by (instance) (
    clamp_min(
      idelta(function_duration_seconds_sum{job="$job", instance=~"$instance(:\\d+)?$", namespace!~"rpc|engine", function_name=~"Storage read|Account read|Account code read|Block hash read"}[$__rate_interval]),
      0
    )
  ),
  "function_name","Block Execution (w/o reads)","instance",".*"
)

scalar(
  max(
    ( max without(instance) (
        last_over_time(block_number{job="$job", instance=~"$instance(:\\d+)?$"}[$__rate_interval])
      )
      == bool
      min without(instance) (
        last_over_time(block_number{job="$job", instance=~"$instance(:\\d+)?$"}[$__rate_interval])
      )
  ))
)

sum by (method) (
  rate(rpc_request_duration_seconds_count{
    job="$job",
    instance=~"$instance(:\\d+)?$",
    namespace="engine"
  }[$__range])
)

sum by (method, error_kind) (
  increase(rpc_requests_total{
    job="$job",
    instance=~"$instance(:\\d+)?$",
    outcome="error"
  }[$__range])
)

100 *
sum by (method, error_kind) (
  increase(rpc_requests_total{
    job="$job",
    instance=~"$instance(:\\d+)?$",
    outcome="error"
  }[$__range])
)
/
on (method) group_left
sum by (method) (
  increase(rpc_requests_total{
    job="$job",
    instance=~"$instance(:\\d+)?$"
  }[$__range])
)

sum by (reason) (
  changes(
    ethrex_p2p_disconnections{
      job="$job",
      instance=~"$instance(:\\d+)?$"
    }[$__rate_interval]
  )
) > 0
